import React, {useState, useEffect} from 'react'
import Poruka from './components/Poruka'
import FormaPrikaz from './components/FormaPrikaz'
import TablicaTransakcija from './components/TablicaTransakcija'
import axios from 'axios'
import transakcijeAkcije from './services/transakcije'
import Podatak from './components/Podatak'

const App = (props) => {
  const [brojid,postavibrojID] = useState(1);
  const [transakcije, postaviTransakcije] = useState([])
  const [ prikaziFormu, postaviPrikazivanjeForme ] = useState(false)

  const manipulirajFormom = (vrijednost) =>{
    if(vrijednost){ postaviPrikazivanjeForme(vrijednost) }
    else{ postaviPrikazivanjeForme(vrijednost) }
  }

  const novaPoruka = (e) => {
    e.preventDefault()

    if (rtbPrihod === false && rtbRashod === false) {
        return
    }
    var vrst = rtbPrihod ? "Prihod" : "Rashod"

    var dat = new Date()
    var noviO = new Podatak(brojid,)
    postavibrojID(brojid+1)
    const noviObjekt = {
        vrsta: vrst,
        datum: dat.getFullYear() + "." + (dat.getMonth() + 1) + "." + dat.getDate(),
        opis: unosOpis,
        iznos: Number(unosIznos)
    }
    porukeAkcije.stvori(noviObjekt)
        .then(res => {
            postaviPoruke(poruke.concat(res.data))
            postaviUnosIznos('')
            postaviUnosOpis('')
            setInputVisible(false)
        })
}
  return (
    <div>
      <div>
        <button onClick={() => manipulirajFormom(true)}>NOVI TROÅ AK</button>
      </div>
      <div>
        {prikaziFormu ? <FormaPrikaz manipuliraj={manipulirajFormom} onSubmit={novaTransakcija} podaci={transakcije} postavipodatke = {postaviTransakcije}/> : null}
      </div>
      <div>
        <button>SAMO PRIHODI</button>
        <button>SAMO RASHODI</button>
        <button>SVE TRANSAKCIJE</button>
      </div>
      <div>
        {transakcije.length !== 0 ? <TablicaTransakcija transakcije /> : null}
      </div>
    </div>
  )
}

export default App
