import React, {useState, useEffect} from 'react'
import Poruka from './components/Poruka'
import FormaPrikaz from './components/FormaPrikaz'
import TablicaTransakcija from './components/TablicaTransakcija'
import axios from 'axios'
import porukeAkcije from './services/poruke'

const App = (props) => {
  // OVO JE VIÅ AK
  const [ poruke, postaviPoruke] = useState([])
  const [ unosPoruke, postaviUnos] = useState('unesi poruku...')
  const [ ispisSve, postaviIspis] = useState(true)
  // SVE DO OVDJE
  //novi dio
  const [transakcije, postaviTransakcije] = useState([])
  const [ prikaziFormu, postaviPrikazivanjeForme ] = useState(false)

  const manipulirajFormom = (vrijednost) =>{
    if(vrijednost){ postaviPrikazivanjeForme(vrijednost) }
    else{ postaviPrikazivanjeForme(vrijednost) }
  }
  
  const porukeZaIspis = ispisSve
  ? poruke
  : poruke.filter(poruka => poruka.vazno === true)

  const promjenaVaznostiPoruke = (id) => {
    const poruka = poruke.find(p => p.id === id)
    const modPoruka = {
      ...poruka,
      vazno: !poruka.vazno
    }
  
    porukeAkcije.osvjezi(id, modPoruka)
      .then(response => {
        console.log(response)
        postaviPoruke(poruke.map(p => p.id !== id ? p : response.data))
      })
  }

  const brisiPoruku = (id) => {
    porukeAkcije.brisi(id)
      .then(response => {
        console.log(response);
        postaviPoruke(poruke.filter(p => p.id !== id))
      })
  }

  useEffect( () => {
    porukeAkcije.dohvatiSve()
    .then(res => postaviPoruke(res.data))
  }, [])

  // const novaPoruka = (e) => {
  //   e.preventDefault()
  //   console.log('Klik', e.target)
  //   const noviObjekt = {
  //     sadrzaj: unosPoruke,
  //     vazno: Math.random() > 0.5      
  //   }
  //   porukeAkcije.stvori(noviObjekt)
  //   .then(res => {
  //     postaviPoruke(poruke.concat(res.data))
  //     postaviUnos('')
  //   })
  // }

  // const promjenaUnosa = (e) => {
  //   console.log(e.target.value);
  //   postaviUnos(e.target.value)
  // }
  return (
    <div>
      <h1>DomaÄi rad 2</h1>
      <div>
        <button onClick={() => manipulirajFormom(true)}>
          NOVI TROÅ AK
        </button>
      </div>
      <div>
        <br/>
        {prikaziFormu? <FormaPrikaz manipuliraj={manipulirajFormom}/>:null}<br/>
      </div>
      <div>
        <button>SAMO PRIHODI</button>
        <button>SAMO RASHODI</button>
        <button>SVE TRANSAKCIJE</button>
      </div>
      <div>
        {transakcije ? <TablicaTransakcija transakcije /> : null}
      </div>
      <div>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
        <br/>
      </div>
      <ul>
        {porukeZaIspis.map(p =>
          <Poruka key={p.id} poruka={p} promjenaVaznosti={() => promjenaVaznostiPoruke(p.id)}
          brisiPoruku={() => brisiPoruku(p.id)} />
        )}        
      </ul>
      <form onSubmit={novaPoruka}>
        <input value={unosPoruke} onChange={promjenaUnosa} />
        <button type='submit'>Spremi</button>
      </form>
    </div>
  )
}

export default App
